mapper.update:
UPDATE userrole
SET
RoleId = #{RoleId}
WHERE UserId = #{UserId}

mapper.select:
SELECT USER.*, role.RoleType, USER.UpdateTime as updateTime FROM USER LEFT JOIN userrole ON userrole.UserId = `user`.UserId LEFT JOIN role ON userrole.RoleId = role.RoleId WHERE `user`.UserStatus LIKE concat('%', #{status}, '%')
AND role.RoleType = #{roleType}
AND USER.UserName LIKE concat('%', #{username}, '%')
LIMIT #{currentPage}, #{PageSize}

SELECT count(*) FROM USER LEFT JOIN userrole ON userrole.UserId = `user`.UserId LEFT JOIN role ON userrole.RoleId = role.RoleId WHERE `user`.UserStatus LIKE concat('%', #{status}, '%')
AND role.RoleType = #{roleType}
AND USER.UserName LIKE concat('%', #{username}, '%')
